import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { User, Coins, Package, TrendingUp, Clock, CheckCircle, Gift } from 'lucide-react';
import axios from 'axios';
import ItemCard from '../components/ItemCard';
import LoadingSpinner from '../components/LoadingSpinner';

const DashboardPage = () => {
  const { user, refreshUser } = useAuth();
  const [userItems, setUserItems] = useState([]);
  const [redemptions, setRedemptions] = useState([]);
  const [itemsRedeemed, setItemsRedeemed] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalListed: 0,
    totalApproved: 0,
    totalRedeemed: 0,
    pointsEarned: 0
  });

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const [itemsResponse, redemptionsResponse, itemsRedeemedResponse] = await Promise.all([
          axios.get('/api/items/user/my-items'),
          axios.get('/api/redemptions/my-redemptions'),
          axios.get('/api/redemptions/my-items-redeemed')
        ]);

        setUserItems(itemsResponse.data);
        setRedemptions(redemptionsResponse.data);
        setItemsRedeemed(itemsRedeemedResponse.data);

        // Calculate stats
        const items = itemsResponse.data;
        const approvedItems = items.filter(item => item.is_approved);
        const redeemedItems = items.filter(item => !item.is_available);
        const pointsEarned = approvedItems.reduce((sum, item) => sum + item.point_value, 0);

        setStats({
          totalListed: items.length,
          totalApproved: approvedItems.length,
          totalRedeemed: redeemedItems.length,
          pointsEarned
        });

        // Refresh user data to get latest points
        await refreshUser();

      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [refreshUser]);

  if (loading) {
    return (
      <div className="min-h-screen bg-secondary-50 flex items-center justify-center">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-secondary-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-secondary-900 mb-2">
            Welcome back, {user?.firstName}! 👋
          </h1>
          <p className="text-secondary-600">
            Here's what's happening in your ReWear journey
          </p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {/* Points Balance */}
          <div className="card">
            <div className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-secondary-600">Available Points</p>
                  <p className="text-2xl font-bold text-primary-600">{user?.points || 0}</p>
                </div>
                <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center">
                  <Coins className="w-6 h-6 text-primary-600" />
                </div>
              </div>
              <div className="mt-4">
                <div className="flex items-center text-sm text-green-600">
                  <TrendingUp className="w-4 h-4 mr-1" />
                  <span>Ready to shop!</span>
                </div>
              </div>
            </div>
          </div>

          {/* Items Listed */}
          <div className="card">
            <div className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-secondary-600">Items Listed</p>
                  <p className="text-2xl font-bold text-secondary-900">{stats.totalListed}</p>
                </div>
                <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                  <Package className="w-6 h-6 text-blue-600" />
                </div>
              </div>
              <div className="mt-4">
                <div className="flex items-center text-sm text-secondary-600">
                  <CheckCircle className="w-4 h-4 mr-1 text-green-500" />
                  <span>{stats.totalApproved} approved</span>
                </div>
              </div>
            </div>
          </div>

          {/* Points Earned */}
          <div className="card">
            <div className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-secondary-600">Points Earned</p>
                  <p className="text-2xl font-bold text-secondary-900">{stats.pointsEarned}</p>
                </div>
                <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                  <Gift className="w-6 h-6 text-green-600" />
                </div>
              </div>
              <div className="mt-4">
                <div className="flex items-center text-sm text-secondary-600">
                  <span>From {stats.totalApproved} approved items</span>
                </div>
              </div>
            </div>
          </div>

          {/* Items Redeemed */}
          <div className="card">
            <div className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-secondary-600">Items Redeemed</p>
                  <p className="text-2xl font-bold text-secondary-900">{redemptions.length}</p>
                </div>
                <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center">
                  <User className="w-6 h-6 text-purple-600" />
                </div>
              </div>
              <div className="mt-4">
                <div className="flex items-center text-sm text-secondary-600">
                  <span>Your sustainable choices</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Activity Sections */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* My Recent Items */}
          <div className="card">
            <div className="p-6">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-semibold text-secondary-900">My Recent Items</h2>
                <a href="/my-items" className="text-primary-600 hover:text-primary-700 text-sm font-medium">
                  View all →
                </a>
              </div>
              
              {userItems.length > 0 ? (
                <div className="space-y-4">
                  {userItems.slice(0, 3).map((item) => (
                    <div key={item.id} className="flex items-center space-x-4 p-3 bg-secondary-50 rounded-lg">
                      <img
                        src={item.images?.[0] ? `/uploads/${item.images[0]}` : 'https://via.placeholder.com/60x60'}
                        alt={item.title}
                        className="w-12 h-12 object-cover rounded-lg"
                        onError={(e) => {
                          e.target.src = 'https://via.placeholder.com/60x60?text=No+Image';
                        }}
                      />
                      <div className="flex-1 min-w-0">
                        <h3 className="font-medium text-secondary-900 truncate">{item.title}</h3>
                        <div className="flex items-center space-x-2 text-sm text-secondary-600">
                          <span>{item.point_value} points</span>
                          <span>•</span>
                          <span className={`font-medium ${
                            item.is_approved 
                              ? item.is_available 
                                ? 'text-green-600' 
                                : 'text-blue-600'
                              : 'text-yellow-600'
                          }`}>
                            {item.is_approved 
                              ? item.is_available 
                                ? 'Available' 
                                : 'Redeemed'
                              : 'Pending'}
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <Package className="w-12 h-12 text-secondary-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-secondary-900 mb-2">No items yet</h3>
                  <p className="text-secondary-600 mb-4">Start by listing your first item!</p>
                  <a href="/add-item" className="btn-primary inline-flex items-center">
                    List Your First Item
                  </a>
                </div>
              )}
            </div>
          </div>

          {/* Recent Redemptions */}
          <div className="card">
            <div className="p-6">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-semibold text-secondary-900">Recent Redemptions</h2>
                <a href="/my-redemptions" className="text-primary-600 hover:text-primary-700 text-sm font-medium">
                  View all →
                </a>
              </div>
              
              {redemptions.length > 0 ? (
                <div className="space-y-4">
                  {redemptions.slice(0, 3).map((redemption) => (
                    <div key={redemption.id} className="flex items-center space-x-4 p-3 bg-secondary-50 rounded-lg">
                      <img
                        src={redemption.images?.[0] ? `/uploads/${redemption.images[0]}` : 'https://via.placeholder.com/60x60'}
                        alt={redemption.item_title}
                        className="w-12 h-12 object-cover rounded-lg"
                        onError={(e) => {
                          e.target.src = 'https://via.placeholder.com/60x60?text=No+Image';
                        }}
                      />
                      <div className="flex-1 min-w-0">
                        <h3 className="font-medium text-secondary-900 truncate">{redemption.item_title}</h3>
                        <div className="flex items-center space-x-2 text-sm text-secondary-600">
                          <span>{redemption.points_used} points</span>
                          <span>•</span>
                          <span>from {redemption.owner_first_name}</span>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="text-xs text-secondary-500">
                          {new Date(redemption.created_at).toLocaleDateString()}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <Clock className="w-12 h-12 text-secondary-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-secondary-900 mb-2">No redemptions yet</h3>
                  <p className="text-secondary-600 mb-4">Browse items to start shopping!</p>
                  <a href="/browse" className="btn-primary inline-flex items-center">
                    Browse Items
                  </a>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="mt-8">
          <div className="card">
            <div className="p-6">
              <h2 className="text-xl font-semibold text-secondary-900 mb-6">Quick Actions</h2>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
                <a href="/add-item" className="btn-outline text-center">
                  <Package className="w-5 h-5 mx-auto mb-2" />
                  List New Item
                </a>
                <a href="/browse" className="btn-outline text-center">
                  <TrendingUp className="w-5 h-5 mx-auto mb-2" />
                  Browse Items
                </a>
                <a href="/my-items" className="btn-outline text-center">
                  <User className="w-5 h-5 mx-auto mb-2" />
                  Manage Items
                </a>
                <a href="/profile" className="btn-outline text-center">
                  <User className="w-5 h-5 mx-auto mb-2" />
                  Edit Profile
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
