import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ArrowRight, Recycle, Users, Coins, TrendingUp, Heart } from 'lucide-react';
import SearchBar from '../components/SearchBar';
import ImageCarousel from '../components/ImageCarousel';
import CategoriesSection from '../components/CategoriesSection';
import ProductGrid from '../components/ProductGrid';
import LoadingSpinner from '../components/LoadingSpinner';

const HomePage = () => {
  const [featuredItems, setFeaturedItems] = useState([]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [featuredResponse, statsResponse] = await Promise.all([
          axios.get('/api/public/featured'),
          axios.get('/api/public/stats')
        ]);
        
        setFeaturedItems(featuredResponse.data);
        setStats(statsResponse.data);
      } catch (error) {
        console.error('Error fetching homepage data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (searchTerm) => {
    if (searchTerm.trim()) {
      navigate(`/browse?search=${encodeURIComponent(searchTerm)}`);
    } else {
      navigate('/browse');
    }
  };

  const handleCategorySelect = (categoryId) => {
    setSelectedCategory(categoryId);
    if (categoryId === 'all') {
      navigate('/browse');
    } else {
      navigate(`/browse?category=${categoryId}`);
    }
  };

  return (
    <div className="min-h-screen bg-secondary-50">
      {/* Header Section with Search */}
      <section className="bg-white shadow-sm border-b border-secondary-100">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center mb-8">
            <h1 className="text-4xl md:text-5xl font-bold text-secondary-900 mb-4">
              Find Your Perfect
              <span className="block text-primary-500">Sustainable Style</span>
            </h1>
            <p className="text-lg text-secondary-600 max-w-2xl mx-auto">
              Discover amazing pre-loved fashion pieces from our community of conscious consumers
            </p>
          </div>
          
          {/* Search Bar */}
          <div className="max-w-3xl mx-auto">
            <SearchBar 
              onSearch={handleSearch}
              placeholder="Search for tops, dresses, accessories..."
              showFilters={true}
            />
          </div>
        </div>
      </section>

      {/* Insights Section - Image Carousel */}
      <section className="py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-secondary-900 mb-4">Community Insights</h2>
            <p className="text-lg text-secondary-600 max-w-2xl mx-auto">
              See how our community is making a difference in sustainable fashion
            </p>
          </div>
          <ImageCarousel />
        </div>
      </section>

      {/* Categories Section */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <CategoriesSection onCategorySelect={handleCategorySelect} />
        </div>
      </section>

      {/* Product Listings Section */}
      <section className="py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <ProductGrid 
            items={featuredItems}
            loading={loading}
            title="Featured Items"
            showViewAll={true}
          />
        </div>
      </section>

      {/* Stats Section */}
      {!loading && (
        <section className="py-16 bg-white">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center mb-12">
              <h2 className="text-3xl font-bold text-secondary-900 mb-4">Our Impact</h2>
              <p className="text-lg text-secondary-600">Together we're building a more sustainable future</p>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-4xl mx-auto">
              <div className="text-center p-6 bg-primary-50 rounded-xl">
                <div className="w-16 h-16 bg-primary-500 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Recycle className="w-8 h-8 text-white" />
                </div>
                <div className="text-3xl font-bold text-primary-600 mb-2">
                  {stats.totalItems || 0}
                </div>
                <div className="text-secondary-600">Items Shared</div>
              </div>
              
              <div className="text-center p-6 bg-blue-50 rounded-xl">
                <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Users className="w-8 h-8 text-white" />
                </div>
                <div className="text-3xl font-bold text-blue-600 mb-2">
                  {stats.totalUsers || 0}
                </div>
                <div className="text-secondary-600">Community Members</div>
              </div>
              
              <div className="text-center p-6 bg-green-50 rounded-xl">
                <div className="w-16 h-16 bg-green-500 rounded-full flex items-center justify-center mx-auto mb-4">
                  <TrendingUp className="w-8 h-8 text-white" />
                </div>
                <div className="text-3xl font-bold text-green-600 mb-2">
                  {stats.totalExchanges || 0}
                </div>
                <div className="text-secondary-600">Successful Exchanges</div>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Call to Action */}
      <section className="py-20 bg-gradient-to-r from-primary-500 to-primary-600">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-3xl font-bold text-white mb-6">
            Ready to Join Our Community?
          </h2>
          <p className="text-xl text-primary-100 mb-8">
            Start your sustainable fashion journey today and earn 50 welcome points!
          </p>
          <div className="flex flex-col sm:flex-row items-center justify-center gap-4">
            <Link 
              to="/register" 
              className="bg-white text-primary-600 hover:bg-primary-50 px-8 py-4 rounded-lg font-medium transition-colors text-lg flex items-center space-x-2"
            >
              <Heart className="w-5 h-5" />
              <span>Join ReWear Free</span>
            </Link>
            <Link 
              to="/browse" 
              className="border-2 border-white text-white hover:bg-white hover:text-primary-600 px-8 py-4 rounded-lg font-medium transition-colors text-lg flex items-center space-x-2"
            >
              <span>Browse Items</span>
              <ArrowRight className="w-5 h-5" />
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
