import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { ArrowLeft, User, Tag, Clock, CheckCircle, X, Coins } from 'lucide-react';
import axios from 'axios';
import LoadingSpinner from '../components/LoadingSpinner';
import SearchBar from '../components/search/SearchBar';

const ItemPage = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeImage, setActiveImage] = useState(0);
  
  useEffect(() => {
    const fetchItemDetails = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/items/${id}`);
        setItem(response.data);
      } catch (err) {
        console.error('Error fetching item details:', err);
        setError('Failed to load item details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchItemDetails();
  }, [id]);
  
  const handleSearch = (searchData) => {
    navigate(`/browse?search=${searchData.term}&category=${searchData.category}`);
  };
  
  if (loading) {
    return (
      <div className="min-h-screen bg-secondary-50 flex items-center justify-center">
        <LoadingSpinner size="lg" />
      </div>
    );
  }
  
  if (error || !item) {
    return (
      <div className="min-h-screen bg-secondary-50 flex items-center justify-center">
        <div className="card p-8 text-center">
          <X className="w-12 h-12 text-red-500 mx-auto mb-4" />
          <h2 className="text-xl font-bold text-secondary-900 mb-2">Error Loading Item</h2>
          <p className="text-secondary-600 mb-4">{error || "Item not found"}</p>
          <button 
            onClick={() => navigate(-1)} 
            className="btn-primary"
          >
            Go Back
          </button>
        </div>
      </div>
    );
  }
  
  // Process item images for display
  const itemImages = item.images && item.images.length > 0 
    ? item.images.map(img => `/uploads/${img}`) 
    : ['https://via.placeholder.com/400x300?text=No+Image'];
  
  return (
    <div className="min-h-screen bg-secondary-50">
      {/* Header Area */}
      <div className="bg-white border-b border-secondary-100 p-4">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold text-secondary-900">Item Listing</h1>
          
          <div className="w-10 h-10 bg-secondary-200 rounded-full flex items-center justify-center">
            {user?.profileImage ? (
              <img 
                src={user.profileImage} 
                alt={`${user.firstName} ${user.lastName}`}
                className="w-full h-full object-cover rounded-full" 
              />
            ) : (
              <User className="w-5 h-5 text-secondary-600" />
            )}
          </div>
        </div>
      </div>

      {/* Navigation Bar */}
      <div className="bg-white shadow-sm border-b border-secondary-100 p-4">
        <div className="max-w-7xl mx-auto">
          <div className="flex flex-col gap-4">
            <button 
              onClick={() => navigate(-1)} 
              className="flex items-center text-secondary-600 hover:text-primary-500 transition-colors"
            >
              <ArrowLeft className="w-5 h-5 mr-2" />
              Back
            </button>
            
            <SearchBar 
              onSearch={handleSearch}
              placeholder="Search for clothing, shoes, accessories..."
            />
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="max-w-7xl mx-auto p-4 mt-6">
        <div className="flex flex-col md:flex-row gap-8">
          {/* Left Side - Main Product Image */}
          <div className="w-full md:w-1/2">
            <div className="card overflow-hidden">
              <div className="relative aspect-square">
                <img 
                  src={itemImages[activeImage]} 
                  alt={item.title}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/400x400?text=No+Image';
                  }}
                />
                {item.is_approved && !item.is_available && (
                  <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                    <div className="bg-red-500 text-white px-4 py-2 rounded-lg font-bold">
                      REDEEMED
                    </div>
                  </div>
                )}
                {!item.is_approved && (
                  <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                    <div className="bg-yellow-500 text-white px-4 py-2 rounded-lg font-bold">
                      PENDING APPROVAL
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
          
          {/* Right Side - Product Information */}
          <div className="w-full md:w-1/2">
            <div className="card p-6">
              <div className="flex justify-between items-start mb-4">
                <h2 className="text-2xl font-bold text-secondary-900">{item.title}</h2>
                <div className="bg-primary-100 text-primary-700 px-3 py-1 rounded-full flex items-center space-x-1">
                  <Coins className="w-4 h-4" />
                  <span className="font-medium">{item.point_value} points</span>
                </div>
              </div>
              
              <div className="flex items-center space-x-4 mb-4">
                <div className="flex items-center space-x-1 text-secondary-500">
                  <Tag className="w-4 h-4" />
                  <span>{item.category_name}</span>
                </div>
                <div className="flex items-center space-x-1 text-secondary-500">
                  <Clock className="w-4 h-4" />
                  <span>{new Date(item.created_at).toLocaleDateString()}</span>
                </div>
                {item.is_approved && (
                  <div className="flex items-center space-x-1 text-green-500">
                    <CheckCircle className="w-4 h-4" />
                    <span>Approved</span>
                  </div>
                )}
              </div>
              
              <div className="grid grid-cols-2 gap-4 mb-6">
                <div className="bg-secondary-50 p-3 rounded-lg">
                  <p className="text-sm text-secondary-500 mb-1">Size</p>
                  <p className="font-medium">{item.size || 'Not specified'}</p>
                </div>
                <div className="bg-secondary-50 p-3 rounded-lg">
                  <p className="text-sm text-secondary-500 mb-1">Brand</p>
                  <p className="font-medium">{item.brand || 'Not specified'}</p>
                </div>
                <div className="bg-secondary-50 p-3 rounded-lg">
                  <p className="text-sm text-secondary-500 mb-1">Condition</p>
                  <p className="font-medium">{item.condition || 'Not specified'}</p>
                </div>
                <div className="bg-secondary-50 p-3 rounded-lg">
                  <p className="text-sm text-secondary-500 mb-1">Type</p>
                  <p className="font-medium">{item.type || item.category_name}</p>
                </div>
              </div>
              
              <div className="mb-6">
                <h3 className="text-lg font-semibold mb-2">Description</h3>
                <p className="text-secondary-700">{item.description}</p>
              </div>
              
              <div className="flex justify-center">
                {item.is_approved && item.is_available && user && user.id !== item.user_id && (
                  <button 
                    onClick={() => navigate(`/redeem/${item.id}`)} 
                    className="btn-primary w-full"
                  >
                    Redeem This Item
                  </button>
                )}
                {user && user.id === item.user_id && (
                  <button 
                    onClick={() => navigate(`/edit-item/${item.id}`)} 
                    className="btn-secondary w-full"
                  >
                    Edit Item
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Product Images Gallery */}
      <div className="max-w-7xl mx-auto p-4 mt-8">
        <h3 className="text-xl font-bold mb-4">Product Images</h3>
        <div className="grid grid-cols-4 sm:grid-cols-5 gap-4">
          {itemImages.map((image, index) => (
            <div 
              key={index}
              className={`card overflow-hidden cursor-pointer ${
                index === activeImage ? 'ring-2 ring-primary-500' : ''
              }`}
              onClick={() => setActiveImage(index)}
            >
              <div className="aspect-square">
                <img 
                  src={image} 
                  alt={`${item.title} - image ${index + 1}`}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/100x100?text=No+Image';
                  }}
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ItemPage;
